mysql_query("insert into tbPropuestas values (460,'".utf8_decode('Salud')."','".utf8_decode('Defensa de la salud y la educación públicas, con salarios dignos, más personal y obras en hospitales y escuelas. Construcción de escuelas, conversión de escuelas de jornada simple a jornada completa y anulación de los subsidios a la enseñanza privada. Fortalecimiento de la prevención en salud y la atención primaria.')."',0, 3, 13)");
mysql_query("insert into tbPropuestas values (461,'".utf8_decode('Educación')."','".utf8_decode('Defensa de la salud y la educación públicas, con salarios dignos, más personal y obras en hospitales y escuelas. Construcción de escuelas, conversión de escuelas de jornada simple a jornada completa y anulación de los subsidios a la enseñanza privada. Fortalecimiento de la prevención en salud y la atención primaria.')."',1, 3, 13)");
mysql_query("insert into tbPropuestas values (462,'".utf8_decode('Patentes')."','".utf8_decode('Reforma del régimen de propiedad intelectual, que les permite a las corporaciones lucrar con los medicamentos, alimentos y semillas. No al patentamiento de la vida. Eliminación de las patentes a los medicamentos y producción estatal de medicamentos genéricos.')."',4, 3, 13)");
mysql_query("insert into tbPropuestas values (463,'".utf8_decode('Propiedad')."','".utf8_decode('Freno a la expulsión de las familias de la Ciudad, y de los campesinos y comunidades originarias de sus territorios. Basta de persecución a los qom y demás pueblos originarios. Restitución de sus tierras. Protección de nuestros lazos culturales y ambientales con la tierra y el hábitat.')."',4, 3, 13)");
mysql_query("insert into tbPropuestas values (464,'".utf8_decode('Deuda Externa')."','".utf8_decode('Auditoría y no pago de la deuda externa ilegítima, destinando esos fondos por ejemplo a un verdadero plan de acceso a la vivienda propia.')."',6, 3, 13)");
mysql_query("insert into tbPropuestas values (465,'".utf8_decode('Vivienda')."','".utf8_decode('Vivienda digna y adecuada, acceso a servicios públicos, bienes y servicios sociales. Regulación del mercado de alquileres. Normativas de gestión y control de la urbanización que reconozcan la función social y comunitaria de la Ciudad y la propiedad privada.')."',5, 3, 13)");
mysql_query("insert into tbPropuestas values (466,'".utf8_decode('Participación')."','".utf8_decode(' Democracia participativa en las políticas públicas y la prestación de los servicios públicos. Presupuesto participativo, en la elaboración, la ejecución y el control.')."',9, 3, 13)");
mysql_query("insert into tbPropuestas values (467,'".utf8_decode('Funcionarios Públicos')."','".utf8_decode('Ley de obligatoriedad para todos los funcionarios políticos (presidente, ministros, senadores, diputados, legisladores, intendentes y concejales) de educar a sus hijos en escuelas públicas y de atenderse en hospitales públicos.')."',9, 3, 13)");
mysql_query("insert into tbPropuestas values (468,'".utf8_decode('Cargos Públicos')."','".utf8_decode('Un freno a la impunidad del poder: revocabilidad de todos los cargos electivos si no cumplen sus promesas, salarios iguales a los de una maestra de doble turno con 10 años de antigüedad, embargo de los bienes personales en caso de corrupción o defraudación al Estado.')."',9, 3, 13)");
mysql_query("insert into tbPropuestas values (469,'".utf8_decode('Poder Judicial')."','".utf8_decode('Ni la reforma oficialista ni la corporación judicial. Verdadera democratización y garantía de acceso a justicia, con juicios por jurados, jueces y fiscales electos por voto popular, sin consejos de la magistratura sujetos al poder político.')."',9, 3, 13)");
mysql_query("insert into tbPropuestas values (470,'".utf8_decode('Trata de Personas')."','".utf8_decode('Combate integral contra la trata de personas para explotación laboral y sexual, el narcotráfico y toda otra forma de delito organizado, que funciona al amparo policial, judicial y político.')."',4, 3, 13)");
mysql_query("insert into tbPropuestas values (471,'".utf8_decode('Policía')."','".utf8_decode('No al Proyecto X, la criminalización del reclamo social y la ley "antiterrorista". Reestructuración total de la policía, con depuración de las cúpulas. Nueva seguridad ciudadana, preventiva, con comisarios electos por voto popular, control vecinal de las comisarías y derecho a la sindicalización policial.')."',3, 3, 13)");
mysql_query("insert into tbPropuestas values (472,'".utf8_decode('Megaminería, Monocultivo y Fracking')."','".utf8_decode('Desincentivar el modelo extractivista y la especulación inmobiliaria. Prohibición de la megaminería, el monocultivo de soja transgénica y el fracking. Anulación de las leyes del pacto PRO-K de negocio inmobiliario. Por un país sin Monsanto, Barrick e IRSA, con regulación estatal, captura de las rentas extraordinarias y, progresivamente, de las rentas ordinarias.')."',6, 3, 13)");
mysql_query("insert into tbPropuestas values (473,'".utf8_decode('Ley de semillas')."','".utf8_decode('Rechazo a la "ley de semillas" promovida por el gobierno nacional junto a Monsanto, que pretende privatizar la vida y acorralar a los campesinos y los pequeños y medianos productores.')."',6, 3, 13)");
mysql_query("insert into tbPropuestas values (474,'".utf8_decode('Medio Ambiente')."','".utf8_decode('Reconocimiento de los derechos de la naturaleza para promover una nueva relación como parte de ella Protección del ecosistema, el agua, la tierra, los bosques nativos, lagos y glaciares. A nivel urbano, menos cemento y más espacios verdes comunitarios.')."',5, 3, 13)");
mysql_query("insert into tbPropuestas values (475,'".utf8_decode('Ética')."','".utf8_decode('Promovemos una ética eco-socialista de carácter social, igualitaria,solidaria, comunitaria, democrática y responsable, que genere modelos de desarrollo alternativos al extractivismo y al consumismo capitalista, hacia la desmercantilización de la naturaleza y los bienes comunes.')."',9, 3, 13)");
mysql_query("insert into tbPropuestas values (476,'".utf8_decode('Energía')."','".utf8_decode('Otra matriz energética es posible. Proponemos el desarrollo de fuentes de energías no contaminantes y alternativas.')."',10, 3, 13)");
mysql_query("insert into tbPropuestas values (477,'".utf8_decode('Alimentación')."','".utf8_decode('Promovemos una alimentación sana y económica, con sabores y nutrientes naturales, sin alimentos transgénicos cultivados con agrotóxicos como el glifosato. Soberanía alimentaria y agroecología. Fomento de la comercialización directa entre productores y consumidores.')."',4, 3, 13)");
mysql_query("insert into tbPropuestas values (478,'".utf8_decode('Obras Públicas')."','".utf8_decode('Planeamiento territorial y plan de obras para prevenir y evitar las inundaciones, el colapso de los servicios públicos y el hacinamiento. Cumplimiento del reciclado y las leyes de Basura Cero, con servicio estatal y no negocio de empresas privadas.')."',5, 3, 13)");
mysql_query("insert into tbPropuestas values (479,'".utf8_decode('Patrimonio Cultural')."','".utf8_decode('Protección y recuperación del patrimonio natural, cultural y arquitectónico. Defensa de la identidad de los barrios frente a la construcción indiscriminada y la especulación inmobiliaria.')."',5, 3, 13)");
mysql_query("insert into tbPropuestas values (480,'".utf8_decode('Costanera')."','".utf8_decode('Por una costanera para todos/as. Recuperación de las costas de los ríos que rodean la Ciudad, con acceso público al camino de ribera. Saneamiento de sus aguas. Incremento de los corredores verdes en la Ciudad, las reservas y los lagos.')."',5, 3, 13)");
mysql_query("insert into tbPropuestas values (481,'".utf8_decode('Transporte')."','".utf8_decode('Las privatizaciones y la corrupción matan: para tener un transporte seguro, eficiente y económico, reestatizar los trenes y el subte con control social. Electrificación de los trenes metropolitanos y reconstrucción de los ramales y la industria ferroviaria. Priorizar la expansión del transporte público más seguro, ecológico, rápido y de mayor capacidad: el subte. Ampliar la red de Premetro integrado al subte. No al metrobús privado y contaminante.')."',7, 3, 13)");
mysql_query("insert into tbPropuestas values (482,'".utf8_decode('Acceso al Transporte')."','".utf8_decode('Efectivo derecho a la movilidad de todas las personas, con accesibilidad física y económica, igualdad en la prestación del servicio, seguridad, confort y sustentabilidad ambiental.')."',7, 3, 13)");
mysql_query("insert into tbPropuestas values (483,'".utf8_decode('Bicisendas')."','".utf8_decode('Sistema público de bicicletas. No a la privatización del servicio y planificación de la red de bicisendas integrada con los otros modos de transporte.')."',7, 3, 13)");
mysql_query("insert into tbPropuestas values (484,'".utf8_decode('Colectivos')."','".utf8_decode('Traspaso de las 33 líneas de colectivos intraurbanas. Creación de nuevas líneas según las necesidades zonales. Mejora de las flotas de colectivos para disminuir los factores contaminantes, incorporando tecnología con energía eléctrica.')."',7, 3, 13)");
mysql_query("insert into tbPropuestas values (485,'".utf8_decode('Recursos Estratégicos')."','".utf8_decode('Nacionalización del petróleo, el gas, la gran minería y otros recursos naturales estratégicos.')."',6, 3, 13)");
mysql_query("insert into tbPropuestas values (486,'".utf8_decode('Puerto')."','".utf8_decode('Traspaso del puerto a la Ciudad. Control público sobre los puertos, el comercio exterior y las agroexportaciones, hoy en manos de transnacionales.')."',9, 3, 13)");
mysql_query("insert into tbPropuestas values (487,'".utf8_decode('Género')."','".utf8_decode('Ley de emergencia contra la violencia de género y los femicidios, con presupuesto suficiente para apertura de refugios, campañas masivas de prevención y subsidios de reinserción laboral y habitacional. Fomento al desarrollo educativo de las jóvenes víctimas de violencia.')."',4, 3, 13)");
mysql_query("insert into tbPropuestas values (488,'".utf8_decode('Salud Reproductiva')."','".utf8_decode('Efectiva aplicación de las leyes de Educación Sexual Integral y de Salud Reproductiva. Cumplimiento del fallo de la Corte Suprema que permite los abortos no punibles. Ley de derecho al aborto legal, seguro y gratuito, para que la mujer pueda decidir. Separación de la Iglesia del Estado.')."',0, 3, 13)");
mysql_query("insert into tbPropuestas values (489,'".utf8_decode('Género')."','".utf8_decode('Revertir la brecha salarial entre hombres y mujeres, que hoy es de un 30% promedio.')."',4, 3, 13)");
mysql_query("insert into tbPropuestas values (490,'".utf8_decode('Género')."','".utf8_decode('Fomento de la participación de la mujer en todas las instancias; cupo del 50% en las listas electorales.')."',9, 3, 13)");
mysql_query("insert into tbPropuestas values (491,'".utf8_decode('Minorías')."','".utf8_decode('Promover la sanción de una nueva ley sobre actos discriminatorios, ya presentada en el Congreso. Promover el Plan de Ciudadanía Plena LGBT, en el marco del Frente Parlamentario por la Igualdad de Derechos para las personas LGBT.')."',4, 3, 13)");
