mysql_query("insert into tbPropuestas values (683,'".utf8_decode('Basta de espionaje a los luchadores')."','".utf8_decode('Basta de espionaje a los luchadores, que se haga pública la base de datos de "Proyecto X" y que se entregue la información a quienes fueron espiados. Basta de infiltración de las organizaciones populares. Cese de la persecución y anulación de las causas judiciales contra los más de 5.000 luchadores obreros y populares. Absolución de los trabajadores de Las Heras.')."',2, 9,23)");
