mysql_query("insert into tbPropuestas values (800,'".utf8_decode('7: con medidas de accesibilidad, sobre todo en SUBTES. Hoy se cumple solo en un 30% existiendo líneas completamente inaccesibles.')."','".utf8_decode('')."',4 , 12, 26)");
mysql_query("insert into tbPropuestas values (801,'".utf8_decode('Equiparación de derechos y oportunidades, La Ciudad debe cumplir con el cupo laboral (5%) de personas con discapacidad que marca la ley en los organismos y empresas del estado, sobre el total de la planta permanente de trabajadores del Gob. de la Ciudad. Como así también con la capacitación simultanea para el puesto asignado.')."','".utf8_decode('')."',4 , 12, 26)");
mysql_query("insert into tbPropuestas values (802,'".utf8_decode('ESCUELA INCLUSIVA: en infraestructura, pero también en las barreras que hoy existen en lo curricular, cultural y en las metodologías que impiden que muchos niños y niñas puedan estudiar en igualdad de condiciones.')."','".utf8_decode('')."',4 , 12, 26)");
mysql_query("insert into tbPropuestas values (803,'".utf8_decode('Certificado Único de Discapacidad (C.U.D.): Documento indispensable para obtener todos los derechos y beneficios que marca la ley: requiere la máxima prioridad. Celeridad en el otorgamiento de turnos para gestionarlo y la atención adecuada. Acelerar y eliminar los tiempos de espera excesivos para su rápido otorgamiento con la adecuada infraestructura, médico – administrativa.')."','".utf8_decode('')."',4 , 12, 26)");
